const myModule=(()=>{"use strict";let h=document.getElementById("result"),a=document.querySelector("#btnPerimSquare"),b=document.querySelector("#btnAreaSquare"),c=document.querySelector("#btnPerimTriangulo"),d=document.querySelector("#btnAreaTriangulo"),e=document.querySelector("#btnDiamCircle"),f=document.querySelector("#btnPerimCircle"),g=document.querySelector("#btnAreaCircle"),i=a=>4*a,j=a=>a*a;a.addEventListener("click",()=>{let a=Number(document.getElementById("inputSide").value),b=`El perimetro de cuadrado es: ${i(a)} cm`;h.innerText=b}),b.addEventListener("click",()=>{let a=Number(document.getElementById("inputSide").value),b=`El cuadrado de cuadrado es: ${j(a)} cm^2`;h.innerText=b});let k=(a,b,c)=>a+b+c,l=(a,b)=>a*b/2;c.addEventListener("click",()=>{let a=document.getElementById("1side"),b=document.getElementById("2side"),c=document.getElementById("3side"),d=Number(a.value),e=Number(b.value),f=Number(c.value),g=`El perimetro del triangulo es: ${k(d,e,f)} cm`;h.innerText=g}),d.addEventListener("click",()=>{let a=document.getElementById("base"),b=document.getElementById("altura"),c=Number(a.value),d=Number(b.value),e=`El area del triangulo es: ${l(c,d)} cm^2`;h.innerText=e});let m=Math.PI,n=a=>2*a,o=a=>a**2*m,p=a=>m*(a*a);e.addEventListener("click",()=>{let a=Number(document.getElementById("radio").value),b=`El diametro del circulo es: ${n(a)} cm`;h.innerText=b}),g.addEventListener("click",()=>{let a=Number(document.getElementById("radio").value),b=`El Area del circulo es: ${o(a)} cm^2`;h.innerText=b}),f.addEventListener("click",()=>{let a=Number(document.getElementById("radio").value),b=`El perimetro del circulo es: ${p(a)} cm`;h.innerText=b})})()